css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("news.bbc.co.uk") {

/* left align page */

.centerbody {width:974px !important;border-left:0px !important;border-right:0px !important;margin-left:0px !important;margin-right:0px !important;}

/* Old style headings */

.nlp {margin: 0px 0px 0px 0px !important;padding: 0px 0px 0px 0px !important;font-weight: bold !important;}
.lp {margin: 0px 0px 0px 0px !important;padding: 0px 0px 0px 0px !important;}
.topStoryH, #popStory h4 {font-weight: bold !important;}


/* remove both mastheads */

.blq-rst, .miitb, .newsbanner {display: none;}

/* 'Popular' list spacing */

ul.popstoryList li {padding-bottom: 0px !important;}

/* Smaller grey 'promo' box */

.promobottombg {margin: 0 0 0 0 !important;height:140px !important;}

/* remove TV/Radio 'advert' */

.fgbi, .filler3 {display: none !important;}

/* less whitespace in lists */

.arr {padding-top: 0px !important;padding-right: 0pt !important;padding-bottom: 0px !important;padding-left: 7px !important;}
}
