css:
@-moz-document domain('42chan.site50.net') {

* {
    font-family:monospace !important;
}
html,
body {
	background:#212121 !important;
	color:#dfdfdf !important;
}
a {
    color: #dfdfdf !important;
    background:none !important;
}
a:hover {
    background:none !important;
}
div.navbar > a {
    color: #dfdfdf !important;
    text-shadow: 2px 2px 3px #232323 !important;
}
div.navbar > a:hover {
    background:none !important;
}
div.adminbar > a {
    color: #dfdfdf !important;
    text-shadow:2px 2px 3px #232323 !important;
}
div.logo {
    font-size: 20px !important;
    color: #cdcdcd !important;
}
div.logo > img[alt="Logo"] {
    display: none !important;
}
div.logo::before {
    font-size: 14px !important;
    content: '42chan' !important;
}
div.replymode {
    background: #212121 !important;
    text-shadow:4px 4px 6px #121212 !important;
}
td.postblock {
    background: #343534 !important;
    color:#dfdfdf !important;
    text-align:center !important;
    border-radius: 4px !important;
    -moz-border-radius: 4px !important;
    -webkit-border-radius: 4px !important;
}
span.postername {
    color: #8a1b1b !important;
}
span.postertrip {
    color: #8a1b1b !important;
}
span.admin {
    color: #530000 !important;
}
td.reply {
    background:#111 !important;
    border-radius:5px 0px 5px 0px !important;
    -moz-border-radius:5px 0px 5px 0px !important;
    -webkit-border-radius:5px 0px 5px 0px !important;
    padding-bottom: 5px !important;
}
td.reply > blockquote:not(a) {
    color:#dfdfdf !important;
}
td.reply > blockquote > a {
    color:#cdcdcd !important;
}
td.highlight {
    background:#333 !important;
    border-radius:5px 0px 5px 0px!important;
    -moz-border-radius:5px 0px 5px 0px!important;
    -webkit-border-radius:5px 0px 5px 0px!important;
    border:none !important;
    padding-bottom: 5px !important;
}
td.highlight > blockquote:not(a) {
    color:#cdcdcd !important;
}
li.current {
    background:#454545 !important;
    border: medium none !important;
    border-bottom: 1px solid #454545 !important;
}
li.tab {
    background:#333 !important;
    border: medium none !important;
    border-bottom: 1px solid #333 !important;
}
span.permalink {
    border-radius: 2px 0px 2px 0px !important;
    -moz-border-radius: 2px 0px 2px 0px !important;
    -webkit-border-radius: 2px 0px 2px 0px !important;
}
h1 {
    background: transparent !important;
    color:#dfdfdf !important;
}
em {
    color:#dfdfdf !important;
}
h2 {
    background:#454545 !important;
}
iframe#menu > html > body > h2 {
    background: #454545 !important;
    border-radius:2px 0px 3px 0px !important;
    -moz-border-radius:2px 0px 3px 0px !important;
    -webkit-border-radius:2px 0px 3px 0px !important;
}
span.plus {
    background: #121212 !important;
    border:none !important;
    color:#dfdfdf !important;
}
a:hover {
    background: #404040 !important;
    padding-left:3px !important;
    width:auto !important;
}
a.boardlink:hover {
    background: #404040 !important;
    padding-left:3px !important;
    width:auto !important;
}
li#removeframes:hover {
    background: #404040 !important;
}
li#sitestyles:hover {
    background:#404040 !important;
}
div#watchedthreads {
    background:#787878 !important;
    border:none !important;
    box-shadow:0px 4px 15px -5px #121212 !important;
    -moz-box-shadow:0px 4px 15px -5px #121212 !important;
    -webkit-box-shadow:0px 4px 15px -5px #121212 !important;
}
div#watchedthreadsdraghandle {
    background:#393939 !important;
    font-family:monospace !important;
}
div.reflinkpreview {
    background:rgba(35,35,35,.75) !important;
    border:none !important;
}

@-moz-document url-prefix('http://42chan.site50.net/ag/') {
    
div.fullhead {
    background: #212121 !important;
    margin-top:7px !important;
}
div.fullhead > div.hborder {
    background: #212121 !important;
}
div.fullhead > div.hborder > div.head {
    background: #212121 !important;
}
div.hborder {
    background: #393939 !important;
}
div.hborder > h2 {
    background: #393939 !important;
}
h1:before {
    content: '42chan - ' !important;
}
div.head {
    background: #393939 !important;
}
div.hborder > div.head > strong {
    color: #dfdfdf !important;
}
div.hborder > h2 {
    background: none !important;
}
form {
    background:#232323 !important;
    color: #dfdfdf !important;
    border-radius: 4px !important;
    -moz-border-radius: 4px !important;
    -webkit-border-radius: 4px !important;
}
div.thread {
    background:#393939 !important;
}

}

}
