css:
@-moz-document url-prefix(http://www.google.com/search?) {

.ft {display:none !important;}
.std {font-family:tahoma !important; font-size:9pt !important;}
input{font-family:tahoma !important; font-size:10pt !important;}

/* search results listing */
.g > .r + table > tbody > tr > .j
{
width:100% !important;
padding-right:8px !important;
text-align:justify !important;
min-width:200px !important;
}

p.g, div.g
{min-height:1px !important;}
/*
this makes the widened results close to each other again.  If you want to have
a larger vertical space, I suggest you add {margin-top:#px !important} and {margin-bottom:#px !important}  (Google's default value = 16px)
*/

/*remove google ad*/
table[style=""][align="right"],    /* froogle */
div[ads],                          /* google  */
div[id^=tpa],                      /* google  */
div[spl],                          /* google  */
table[width="25%"][align="right"], /* google  */
table[class=lads],                 /* google  */
div[class="rh"]                    /* mail    */
{display: none !important;}

}

@-moz-document url-prefix(http://www.google.com/webhp?), url-prefix(http://www.google.com/) {
input{font-family:tahoma !important; font-size:10pt !important;}
}
