css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deuslovult.org") {

  /* 
     ATENÇÃO! Caso vá se usar esse código no servidor Wordpress, 
              retirar todos os !important possíveis.                */

  /* post header */
  .entry-meta { display: none !important }
 
  /* post footer */
  .entry-taxonomy, .pagination { display: none !important }
 
  /* post social widgets */
  .addthis_toolbox, .addthis_default_style { display: none !important }
  .snap_nopreview, .sharing, .robots-nocontent { display: none !important }
  .topsy_widget_data, topsy_theme_blue { display: none !important }
  
  /* oculta flas bloqueado RatingWidget.swf */
  .rw-js-container { display: none !important }
  
  #text-506275829, /* janua coeli */
  #text-506275825, /* quem sou eu */
  #text-417053781, /* deus lo vult */
  #text-445693921, /* publicidade */
  #text-506275821, /* prêmios e reconhecimento */
  #text-506275827, /* siga-nos */
  #text-506275822, /* torne-se um fã */
  #text-417054261 /* estatísticas do blog */
    { display: none !important }
  
  /* #site-footer { display: none !important } */ /* era o BUG da altura! */  
  #site-footer > small { 
    text-align: center !important;
    color: gray !important;
    font-style:italic !important;
  }
  
  #site-footer > small:after { 
    content: ' Enhanced with Deus lo Vult! Suggestions, a Stylish theme by Alexandre Magno, blog reader.';
    font-style:italic !important;
  } 
  
  /* stats? */
  #wpstats { display: none !important }
  
  /* estrelas de votação */
  .rw-ui-container { display: none !important }
  
  .comment blockquote { 
    margin: 0px !important;
    padding: 0px !important; 
  }
  
  .comment blockquote p { 
    margin: 0px !important;
    padding: 10px !important; 
  }
  
  .entry-content { text-align: justify !important }
  
  .entry-content blockquote { 
    margin: 0px !important;
    padding: 0px !important; 
  }
  
  .entry-content blockquote p { 
    margin: 0px !important;
    padding: 10px !important;
    padding-top: 0px !important;
  }
  
  /* BEGIN disqus styles */
      
  .dsq-comment-text blockquote { 
    margin: 0px !important;
    padding: 0px !important; 
    padding-left: 20px !important;
    padding-right: 20px !important;
  }
  
  .dsq-comment-text blockquote p { 
    margin: 0px !important;
    padding: 10px !important; 
  }
  
  .dsq-widget-comment blockquote { 
    margin: 0px !important;
    padding: 7px !important; 
    padding-left: 17px !important; /* talvez seja melhor identificar uma medida "em" */
    padding-right: 17px !important;
  }
  
  .dsq-widget-comment blockquote p { 
    margin: 0px !important;
    padding: 10px !important; 
  }
  
  /* destaca moderadores */
  #dsq-content .dsq-moderator .dsq-comment-header {
    background: #EBF0F5 !important;
  }

  /* END disqus styles */
  
  /* site footer */
  .entry-footer { border-top: 0px !important; }
  
  /* correção aviso de netiqueta (final de cada post) */
  .entry-footer > p { 
    font-size: 0.9em !important;
    background-color: #FFFFCC !important;
    padding: 5px !important;
    text-align: center !important;
  }
  
  /* BEGIN ajustes textura */
  
  body {
    color: #333 !important;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/www-refreshbg-vflC3wnbM.png) 0 0 !important;
    background-color: #EBEBEB !important;
    background-repeat: repeat !important;
    
    /*height: 5000px ! important; /* TODO teria de ser dinâmico (javascript) */
    /* erradamente eu dei atenção à linha acima */
  }
  
  /*html>body #wrapper{}*/
  
  #wrapper {
    background-color: white !important;
    /*height: 5000px ! important; /* TODO teria de ser dinâmico (javascript) */ 
    /* erradamente eu dei atenção à linha acima */
    
    padding-top: 1.2em !important;
    
    border-radius: 1em;
    
    -moz-box-shadow: 0 0 3px #888;
    -webkit-box-shadow: 0 0 3px #888;
    box-shadow: 0 0 3px #888;
    
    border-bottom-left-radius: 0;
    border-bottom-right-radius: 0;
    border-bottom-width: 0;

  }
  
  /* END ajustes textura */
  
  /* site header image */
  
  #site-header { 
    border-radius: 0.55em;
    
    -moz-box-shadow: 0 0 5px #888;
    -webkit-box-shadow: 0 0 5px #888;
    box-shadow: 0 0 5px #888;  
    
    height: 120px !important;
    margin-bottom: 50px !important;
  }
  
  /* site header title */
  #site-title { 
    color: white !important;
    background-color: #595F5D !important;  
    
    opacity:0.55;
	-moz-opacity: 0.55;
	filter: alpha(opacity=55);
	
	border-radius: 0.2em;
	box-shadow: black 0.2em 0.2em 0.1em;
    
    margin: 0px !important;
    padding: 0px !important;
    padding-top: 15px !important;
    padding-bottom: 15px !important;
    padding-left: 5px !important;
    padding-right: 5px !important;
    width: 280px !important;
    position: realtive;
    margin-top: 60px !important;
    
    font-size: 3em !important;
    
    height: auto !important;

  } 
  
  /* site header description */
  #tag { 
    position: absolute !important;
    color: gray !important; 
    top: 200px !important;
    
    font-size: 0.94em !important;
  } 
  
  /* correçao na caixa de pesquisa */
  #cse-search-box input[type=text] { width: 230px !important }
  
  /* correção das fontes nos checkboxes */ 
  .subscribe-to-comments {
    font-size: 0.833em !important;
    font-family: arial, helvetica, 'lucida grande', 'lucida sans unicode', verdana, sans-serif !important;
  }
  
  /* sidebar titles */
  
  h1.widgetTitle { 
    border-radius: 0.5em; 
    border-bottom-left-radius: 0;
    border-bottom-right-radius: 0;
    padding: 5px !important;
  }
  

  /*  Ref.: 
  
   - http://www.css3.info/preview/box-shadow/ 
   - http://www.css3.info/preview/rounded-border/

                                                    */


}
