css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
#posts .post .post_controls .reply_button {
width: 37px;
background: transparent !important;
color: #818B98;
}

#posts .post .post_controls .reply_button:after {
content: "reply";
}

#posts .post .post_controls .reblog_count span:after {
content: " notes";
}

#posts .post .post_controls .reblog_button {
background: transparent !important;
width: 45px !important;
height: 17px;
vertical-align: middle;
}

#posts .post .post_controls .reblog_button:after {
content: "reblog";
}

#posts .post .post_controls .MissingE_experimental_reply {
width: 36px !important;
}
}
