css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.live.com") {

/* making a little bit more space on top... */
#Middle {
    margin-top: 8px;
}

/* making a little bit more space on the toolbar for the buttons... */
#InboxToolbar {
    width: 651px !important;
    margin-top: -6px !important;
}

/* actual buttons */
#InboxToolbar .ToolbarItem, #InboxOptionsToolbar {
    border: 1px solid #ccc;
    margin-right: 4px !important;
    margin-top: 4px !important;
}

#InboxToolbar .ToolbarItem:hover, #InboxOptionsToolbar:hover {
    border: 1px solid #aaa;
    background-color: #f5f4f4;
    text-decoration: none;
}

/* making space for even more buttons... */
ul.FloatRight {
    margin-top: -6px !important;
}

/* styling "New" button to stand out a little... */
#InboxToolbar .ToolbarItemFirst {
    padding-left: 5px !important;
    background-color: #d9f1fa;
    border: 1px solid #84d4f3;
}

#InboxToolbar .ToolbarItemFirst:hover {
    background-color: #c1e9f8;
    border: 1px solid #61c7ef;
    text-decoration: none;
}

/* unstyling dividers */
#AfterMoveToPipe, #AfterNewPipe, #AfterForwardPipe {
    border: 1px solid white !important;
}

/* making icon buttons a little bit prettier... */
a#PrintMessages, a#Refresh {
    padding: 2px 4px 1px 4px !important;
}

/* kthxbye */

}
