css:
@-moz-document domain("imdb.com") {
/* 
 * Name:    IMDb – Fixed Navigation Position
 * Title:   Just some small personal fixes that I prefer when browsing IMDb
 * Author:  Crystallis
 * Version: 0.8
 *
 */

#supertab,
#maindetails_sidebar_top,
div[class^="aux-ad-widget"],
.watch-bar,
#amazon-affiliates,
#bottom_ad_wrapper,
#swiki_ad_spacer,
.top-rhs,
#btf_rhs2_wrapper {
display: none ! important;
}
#navbar {
margin: 0px auto auto -0px ! important;
position: fixed ! important;
-webkit-box-shadow: 0 1px 15px 0px black ! important;
-moz-box-shadow: 0 1px 15px 0px black ! important;
box-shadow: 0 1px 15px 0px black ! important;
}
#pagecontent {
padding-top: 80px ! important;
}
.pinned-top {
padding-top: 95px ! important;
}
}
