css:
@-moz-document domain('icanhazchat.com') {
body {
    background: #393939 !important;
    height: auto !important;
}

.body_container {
    background-color: !important;
}

#header, #camControl, #activeUserList {
    background-color: #1E1E1E !important;
}

#activeUserList a:link, #activeUserList a:active, 
#activeUserList a:visited, #activeUserList a:hover,
body, #activeUserList, #txtMsg, #txt, #chat_container {
    color: #2C97DB !important;
}

#txtMsg, #txt {
	/*background-color: #545454 !important;*/
	overflow-x: hidden !important;
}

.command_bar {
	border-color: #1E1E1E !important;
}

#panelHeader, #ohhai, #footer, #back {
    display: none !important;
}

#txtMsg, #txt, .rounded_square, #activeUserList, .body_container {
	border-style: none !important;
	border-radius: 0px !important;
}

#activeUserList {
    overflow: none !important;
    top: 485px !important;
    height: 70px !important;
}

#camControl {
    left: 500px !important;
    top: 25% !important;
}

#topicContainer {
	width: 430px !important;
}

#chat_container {
	margin: 5px !important;
}

#txtMsg {
	width: 97% !important;
}

#signout {
	top: 25% !important;
	left: 90% !important;
}

#topic {
	font-size: 13px !important;
}

.smallFont img {
	width: 25px !important;
	height: 25px !important;
}
}
