css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("i-forge.net") {

body {
background: #151515 !important;
color: #d0d0d0 !important;
}

span{
background-color: #151515 !important;
}
span.message, span.lc-elem , span.thread-links ,span.author, span.date, span.id, span.time{
background-color: transparent !important;
}
span.one{
color: #cc6600 !important;
}
span.b{
color: #990000 !important;
}

label{
color: #f0a000 !important;
}
textarea{
background-color: #151551 !important;
}

sup{
color: #f0a000 !important;
}

div.main, div.page-wrapper{
background: #151515 !important;
background-color: #151515 !important;
}

div.infobox-add, div.post, div.all, div.w{
background-color: #404040 !important;
}

div.item{
background-color: #202020 !important;
}

kbd.lc-elem{
background-color: #404040 !important;
color: #c01010 !important;
}

a{
color: #990000 !important;
}

a.t{
color: #b0b0b0 !important;
border: 0px solid rgb(32, 32, 32) !important;
}

input.phrase{
color: #ffffff !important;
border-color: -moz-use-text-color -moz-use-text-color gray !important;
border-bottom-color: gray !important;
background-color: #404040 !important;
}

}
