css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("https://zimbra.vmware.com/*") {

/*-----------hue value=252-----------*/

p, th, td, div, select, input[type="text"], input[type="password"], input[type="file"], textarea, button, .MsgBody* {
font-family: "Myriad Pro", "Helvetica Neue",Helvetica,"Nimbus Sans L",Arial,"Liberation Sans",sans-serif;
font-size: 11px;
}

/*-----------Mail-----------*/
.row{
height: 25px;
padding-top: 8px;
}
.selected, .row-selected, .row-selected-dragproxy, .row-selected-disabled {
background-color: #B0D1FF!important;
border-top-color: #5199FF!important;
}


/*-----------Calendar-----------*/

.appt_body, .appt_30_body, .appt_allday_body, .appt_30_body>table {
border-color: #82a3aa;
-webkit-box-shadow: none;
box-shadow: none;
}
.zmappt-no-response {
background-color: #3d5623;
}

.appt_body tr,  .appt_30_body>table{
 background-color: #b5d1d3 !important;
}

.zmappt-busy {
background-color: #b5d1d3;
}
.zmappt-busy:parent{
 background-color: red !important;

}
.appt_time {
color: #3d7284;
}

.appt_name,  .appt_30_body>table {
background-color: #b5d1d3;
color: #3d7284;
}

div .calendar_heading_day_today-selected, .calendar_heading_day_today { 
 background-color: #E89B72!important;
 color: #1D1D1B;
 font-weight: bold;
}
}
