css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("tumblr.com")



{



/*text&background*/



html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer



{

color: white !important;

font-family: georgia !important;

background: black !important;

}



select, button, div#bottom



{

background: black !important;

}



div, span, a



{

background-color: black !important;

}



legend, select, input, span, code, textarea, pre, label, div, dd, label



{

color: white !important;

}





/*links*/



a



{

color: #0040ff !important;

}



a:visited



{

color: #224375 !important;

}



/*highlight*/



textarea, pre, code, label



{

background: #333333 !important;

}



}
