css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(slashdot.org) {

    .commentBody {
        padding-bottom: 0 !important;
    }

    .commentSub b {
        color: inherit !important;
        background: inherit !important;
    }

    .commentSub :link,
    .commentSub :visited {
        padding: 0 !important;
        background: inherit !important;
        color: rgb(0, 85, 85) !important;
    }

    .commentSub :link:before,
    .commentSub :visited:before {
        content: "[ " !important;
    }

    .commentSub :link:after,
    .commentSub :visited:after {
        content: " ]" !important;
    }

    .nbutton {
        color: inherit !important;
        background: inherit !important;
    }

}
