css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vampirefreaks.com") {

body, html{ 
background-color: #222!important;
}
.leftnav{
background-color: #111 !important;
}
a:link, a:visited { font: 9pt tahoma !important; color: silver !important; text-decoration: none !important; }
a:hover { font: 9pt tahoma !important; color: #fff !important; font-style: italic !important; background: transparent !important; text-decoration: none !important; } 
a, #menu0 div, td, div{
font-family: calibri, veranda, arial!important;
font-size: 12px!important;
}
.menu2{
color: #fff !important;
}
body, table{
font-size: 12px!important;
}
.maintop td[align] img, .mainheading td[width], .side_td[align=right]{
display: none !important;
}
.maintop td[align]{
height: 45px !important;
background-image: url('http://i30.tinypic.com/1196zar.png') !important;
background-repeat: no-repeat !important;
background-attachment: scroll !important;
background-position: center center !important;
}
.side_td, .leftnav{
text-align: center!important;
}
.side_td > .leftnav{
width: 150px !important;
display: table !important;
}
}
