css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mokk.me"){

.mw-viewport{ height:460px!important; }

.mw-switch, .widget-list, .widget-list LABEL{ cursor:pointer; }

/* LABEL widths */
.widget-list LABEL{ width:260px!important; }
.widget-switchLine LABEL{ width:180px!important; }

/* LISTS */
UL .widget-list:first-child{ border-top-left-radius:10px; border-top-right-radius:10px; }
UL .widget-list:last-child{ border-bottom-left-radius:10px; border-bottom-right-radius:10px; }

/* 3 BUTTON to NAV BAR */
.widget-button3 {
  z-index:9999;
  background-color: black;
  position: absolute!important;
  left: 0px;
  top: 393px;
  padding: 0 8px;
  width: 304px;
  height: 50px;
}
.widget-button3 a {
  font-size: .6em;
  letter-spacing: 1px;
  background-color: transparent!important;
  color: #eee!important;
  position: relative;
  top: 20px;
  border-width: 0!important;
  background-color: rgba(200,200,200,.3)!important;
}


}
