css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rockpapershotgun.com") {

    div.block a {
        color: #0000f0 !important;
    }

    div.commentmetadata a {
        color: #0000f0 !important;
    }

    div.block a:hover, div.commentmetadata a:hover {
        text-decoration: underline !important;
    }

    div.block h2 a {
        color: #2D2D2D !important;
    }
    div.content {
    width:900px !important;
    }

    div.body_wrapper {
    width:1120px !important;
    }
    
    div#content {
    width:1210px !important;
    }
    
    body, p, a, div.content, h1, h2, h3 {
    font-family:"Bouwsma Text",Hd,"Warnock Pro",Constant,"Hiroshige ATT","Charis SIL","Tinos","Gentium","ResagokrB","Qlassik Ro","Trebuchet MS","Constantia","Cambria","Galette Ro","Linux Biolinum","Charis SIL" !important;
    }
    
   /* p,li {
    font-size:18px !important;
    }*/
    
    h2 {
    font-size:21px !important;
    }
    

}
