css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("distrowatch.com") {

/* GENERAL */
a {
  text-decoration: none !important;}
a:hover {
  color: blue !important;
  text-decoration: underline !important;}
a:visited {
  color: purple !important;}
input, select {
  /* background-color: #f1f1f1 !important; */
  background: -moz-linear-gradient(center top , #ffffff 0%, #f1f1f1 100%) repeat scroll 0% 0% transparent !important;
  border-radius: 5px !important;
  color: #000000 !important;}
form#cse-search-box div input {
  background-color: #ffffff !important;}

/* NAVIGATION TABLE */
table, th.NavMenu {
  border: 1px !important;
  border-radius: 5px !important;}
  
/* MAIN */
th.Invert {
  border-color: rgb(11, 59, 36) /* dark Green */ !important;
  border-top-left-radius: 5px !important;
  border-top-right-radius: 5px !important;}
td.News1 {
  border-left-color: rgb(246, 237, 200) /* Peach */ !important;
  border-right-color: rgb(246, 237, 200) /* Peach */ !important;
  border-bottom-color: rgb(246, 237, 200) /* Peach */ !important;
  border-top-color: rgb(253, 255, 227) /* Beige */ !important;
  border-bottom-left-radius: 5px !important;
  border-bottom-right-radius: 5px !important;}
table.News, td.NewsHeadline, td.NewsDate {
  border-color: rgb(246, 237, 200) /*Peach*/ !important;
  border-radius: 5px !important;}
  
/* SIDEBARS */
td.News {
  border-color: rgb(246, 237, 200) /* Peach */ !important;
  /* td.News is too broad 
  border-bottom-left-radius: 5px !important;
  border-bottom-right-radius: 5px !important;
  */ }
th.News, td.phr1, td.phr2, td.phr3 {
  border-color: rgb(246, 237, 200) /* Peach */ !important;}

/* DISTRO PAGES */
td.TablesTitle {
  border-left-color: rgb(246, 237, 200) /* Peach */ !important;
  border-right-color: rgb(246, 237, 200) /* Peach */ !important;
  border-bottom-color: rgb(246, 237, 200) /* Peach */ !important;
  border-top-color: rgb(253, 255, 227) /* Beige */ !important;
  border-bottom-left-radius: 5px !important;
  border-bottom-right-radius: 5px !important;}
td.Info, th.Info, tr.Background td, th, td {
  border-color: rgb(246, 237, 200) /* Peach */ !important;}

}
