@-moz-document domain("topics.scmp.com") {
/* Change background colour*/
body,ul,td{background: whitesmoke!important;}

/* Top: Remove Header, Content, rhs */
#publicHeader, #publicTopNav,
#publicWeatherAndSearch, img[src*="title_image"], 
#articlePagination,
#publicArticleMainStoryTopRht,
#publicGoogleAd, #publicContentsRightCol,
#footer 
{display:none !important;}

/* Make it full width */
#publicContentsLeftCol,#publicArticleMainStory {width:100% !important;}

/* White Space */
#publicMainContents,#publicArticleMainStory,
#publicArticleMainStoryTop,
.publicArticleMainStoryLine {margin:0 !important;}
#publicArticleMainStoryDate {margin-bottom:4px !important;}

#publicArticleMainStory {margin-bottom:300px !important;}
}
