css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('file://') {

body:after { 
    display: block;
    content: "960px+";
    background: #abc123; -moz-border-radius: 5px;
    color: #fff;    display: block; font-size: 11px;
    cursor: pointer; font-family: Tahoma, Arial, sans-serif;
    height: 24px;  line-height: 24px;    padding: 0 6px;
    position: fixed;    top: 10px;    left: 10px; opacity: 0.5;
    text-align: center; text-shadow: 1px 1px 0 rgba(0,0,0,0.5); 
    width: 48px;   z-index: 9999;
}

@media only screen and (max-width: 959px) {
        body:after { background: #abb123; content: "< 960px"; }
}

@media only screen and (max-width: 767px) {
        body:after { background: #aba123; content: "< 768px"; }
}

@media only screen and (max-width: 479px) {
        body:after { background: #aa9000; content: "< 480px"; }
}


}
