css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("5irc.com") {
HTML > BODY > TABLE:first-child + TABLE > TBODY > TR:first-child{
display:none;
}

HTML > BODY > TABLE:first-child + TABLE > TBODY > TR:first-child + TR{
display:none;
}

HTML > BODY > TABLE:first-child + TABLE > TBODY > TR:first-child + TR + TR{
display:none;
}

HTML > BODY > TABLE:first-child + TABLE > TBODY > TR:first-child + TR + TR + TR > TD:first-child + TD + TD{
display:none;
}

HTML > BODY > TABLE:first-child + TABLE > TBODY > TR:first-child + TR + TR + TR > TD:last-child{
display:none;
}

HTML > BODY > TABLE:first-child + TABLE + TABLE + TABLE > TBODY > TR > TD{
display: none;
}

HTML > BODY > TABLE:first-child + TABLE + TABLE > TBODY > TR:first-child > TD:first-child > A:first-child{
display:none;
}

HTML > BODY > TABLE:first-child + TABLE + TABLE > TBODY > TR:first-child > TD:first-child > A:first-child + A{
display:none;
}

HTML > BODY > TABLE:first-child + TABLE + TABLE > TBODY > TR:first-child > TD:first-child > A:first-child + A + IMG{
display: none;
}

HTML > BODY > TABLE:first-child + TABLE + TABLE > TBODY > TR:first-child > TD:first-child > A:first-child + A + IMG + IMG{
display: none;
}

HTML > BODY > TABLE:first-child + TABLE + TABLE > TBODY > TR:first-child > TD:first-child > A:first-child + A + IMG + IMG + A{
display: none;
}



}


@-moz-document url-prefix(http://www.5irc.com/content.asp) {
HTML > BODY > DIV:first-child + SCRIPT + SCRIPT + TABLE + TABLE > TBODY > TR:last-child > TD > TABLE:first-child{
display:none;
}


}
