css:
body  { font-family: Serif, Verdana; font-size: 11pt; background: #000000; }
td    { font-family: Serif, Verdana; font-size:10pt; color:#fcfcfc; }
table { font-family: Serif, Monospace; background: #2f4f4f; }
font { font-size: 11pt; color: #FFe4b5; }

.admin td   { font-family: Serif, Verdana; font-size:8.5pt; color:#708090; }
.subtext td { font-family: Serif, Verdana; font-size:  8pt; color:#828282; }

input    { font-family: Serif, Arial, Verdana; font-size:10pt; color:#000000; }
input[type="submit"] { font-family:Verdana; }
textarea { font-family: Monospace; font-size:10pt; color:#000000; }

a:link    { color:#ffe4c4; text-decoration:none; } 
a:visited { color:#ffe4c4; background: #8fbc8f; text-decoration:none; }

.default { font-family: Serif, Arial, Verdana; font-size: 10pt; color:#828282; }
.admin   { font-family: Serif, Arial, Verdana; font-size:8.5pt; color:#000000; }
.title   { font-family: monospace; font-size: 10pt; color:#c0c0c0; }
.adtitle { font-family: Serif; font-size:  9pt; color:#c0c0c0; }
.subtext { font-family: monospace; font-size:  7pt; color:#8fbc8f; }
.yclinks { font-family: serif; font-size:  8pt; color:#c0c0c0; }
.pagetop { font-family: monospace; font-size: 10pt; color: #8fbc8f; }
.comhead { font-family: monospace; font-size:  8pt; color:#c0c0c0; }
.comment { font-family: Serif; font-size:  9pt; }
.dead    { font-family: Serif; font-size:  9pt; color:#dddddd; }

.comment a:link, .comment a:visited { text-decoration:underline;}
.dead a:link, .dead a:visited { color:#dddddd; }
.pagetop a:visited { color:#fcfcfc;}
.topsel a:link, .topsel a:visited { color:#ffffff; }

.subtext a:link, .subtext a:visited { color: #fcfcfc; }
.subtext a:hover { text-decoration:underline; }

.comhead a:link, .subtext a:visited { color:#fcfcfc; }
.comhead a:hover { text-decoration:underline; }

.default p { margin-top: 8px; margin-bottom: 0px; }

.pagebreak {page-break-before:always}

pre { overflow: hidden; padding: 2px; }
pre:hover {overflow:auto}
