@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wolfsbos.nl") {

#menupart .mainMenuItemActive, #menupart .mainMenuItem a:hover {
    background: darkblue !important;
}
#header {
    height: 0px !important;
}
#menu {
    background: url("http://fc01.deviantart.net/fs10/i/2006/153/0/8/Water_Background_Stock_by_Kansheera_Stock.jpg") !important;
    top: 30px !important;
}
#contentpart {
    margin-top: 60px !important;
}
#menupart .mainMenuItem:hover ul {
    background: url("http://fc01.deviantart.net/fs10/i/2006/153/0/8/Water_Background_Stock_by_Kansheera_Stock.jpg") !important;
}
#menupart .mainMenuItem:hover ul li:hover .subSubMenu:hover li a {
    background: url("http://fc01.deviantart.net/fs10/i/2006/153/0/8/Water_Background_Stock_by_Kansheera_Stock.jpg") !important;
}
#menupart .mainMenuItem:hover ul li:hover .subSubMenu:hover li a:hover {
    background: darkblue !important;
}
body {
    background: url("http://img846.imageshack.us/img846/2727/lettersd.png") repeat scroll center top transparent !important;
}
h1, h2, h3, h4, h5, h6 {
    color: darkblue !important;
}
#content #contentpart .listtitle {
    background: none repeat scroll 0 0 darkblue !important;
    border: 0px !important;
}
#content #contentpart .listrow2 {
    background: none repeat scroll 0 0 lightblue !important;
}
#content {
    background: url("http://img21.imageshack.us/img21/2314/sdm1.gif") no-repeat scroll center bottom transparent !important;
}
.widgetBottom form .button {
    background: none repeat scroll 0 0 blue !important;
}
#copyright_text {
    font-size: 0 !important;
}
.nieuwskop {
    background: none repeat scroll 0 0 darkblue !important;
}
.nieuwstitel a {
    color: black !important;
}
.nieuwsitem {
    background: #FFFFFF !important;
}
#contentpart #objectWrapper692 .centerizer {
    border: 1px solid grey !important;
}





}
