css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wolfsbos.nl") {

#menupart .mainMenuItemActive, #menupart .mainMenuItem a:hover {
    background: none repeat scroll 0 0 grey !important;
}
#header {
    height: 0px !important;
}
#menu {
    background: url("http://www.freefoto.com/images/9909/01/9909_01_8584---Winter-trees-with-a-grey-sky_web.jpg") no-repeat !important;
    background-size: 100% !important;
    top: 30px !important;
}
#contentpart {
    margin-top: 60px !important;
}
#menupart .mainMenuItem:hover ul {
    background: url("http://www.freefoto.com/images/9909/01/9909_01_8584---Winter-trees-with-a-grey-sky_web.jpg") no-repeat !important;
}
#menupart .mainMenuItem:hover ul li:hover .subSubMenu:hover li a {
    background: url("http://www.freefoto.com/images/9909/01/9909_01_8584---Winter-trees-with-a-grey-sky_web.jpg") !important;
}
#menupart .mainMenuItem:hover ul li:hover .subSubMenu:hover li a:hover {
    background: none repeat scroll 0 0 grey !important;
}
body {
    background: url("http://img843.imageshack.us/img843/8494/lettersgrijs.png") repeat scroll center top transparent !important;
}
h1, h2, h3, h4, h5, h6 {
    color: #202020 !important;
}
#content #contentpart .listtitle {
    background: none repeat scroll 0 0 grey !important;
    border: 0px !important;
}
#content #contentpart .listrow2 {
    background: none repeat scroll 0 0 lightgrey !important;
}
#content {
    background: url("http://img21.imageshack.us/img21/2314/sdm1.gif") no-repeat scroll center bottom transparent !important;
}
.widgetBottom form .button {
    background: none repeat scroll 0 0 darkgrey !important;
}
#copyright_text {
    font-size: 0 !important;
}
.nieuwskop {
    background: none repeat scroll 0 0 grey !important;
}
.nieuwstitel a {
    color: black !important;
}
.nieuwsitem {
    background: lightgrey !important;
}
#contentpart #objectWrapper692 .centerizer {
    border: 1px solid grey !important;
}


}
