css:
/*make tabs apart from the current one transparent/glass*/

tab:not([selected="true"]){
      background-image:-moz-linear-gradient(
      rgba(201,214,227,0.5), rgba(201,214,227,0.5)
      ) !important;
      }

tab:not([selected="true"]):hover{
      background-image:-moz-linear-gradient(
      rgba(255,255,255,0.75), rgba(201,214,227,0.5)
      ) !important;
      }

/*make the new tab button glass/transparent*/

.tabs-newtab-button {
      background-image:-moz-linear-gradient(
      rgba(201,214,227,0.5), rgba(201,214,227,0.5)
      ) !important;
      }

.tabs-newtab-button:hover {
      background-image:-moz-linear-gradient(
      rgba(255,255,255,0.75), rgba(201,214,227,0.5)
      ) !important;
      }
