css:
@-moz-document domain("battlelog.battlefield.com") {
/* save space on server browser */
#serverguide-filtertoggle.serverguide-filtertoggle-expanded .serverguide-header-refresh-button {display: inline-block !important;}
div#serverguide-apply-filters {display:none !important;}
div#serverguide-filters {height: auto !important; min-height: auto !important;}
div#serverguide-filters .serverguide-filter {height: auto !important; min-height: 0 !important;}
div#serverguide-filters .serverguide-filter-extra {height: auto !important; min-height: 0 !important;}
div#serverguide-filters .serverguide-filter-mapmode-wrapper {height: auto !important; min-height: 0 !important;}
div#serverguide-middle h1.base-big-title {margin: 1px 0 !important; font-size: 0 !important;}
/* fix disappearing refresh button */
#serverguide-filtertoggle.serverguide-filtertoggle-expanded div.serverguide-filterlist {max-width: 699px !important;}
#serverguide-filtertoggle.serverguide-filtertoggle-expanded span.serverguide-filter-overlay {width: 0px !important;}
}
