css:
@-moz-document domain("sports.ru") {
body {font: normal 63.5% Lucida Grande, Helvetica, sans-serif; color: #333; background:#fff;}
.short-text {color: #333;}
.leftPart .news, .rightPart .news {font-size: 11px; line-height: 14px; }
.article-textBlock P {font-size:13px; line-height: 15px; margin:0 0 18px;}
.article-textBlock > H3 {font: bold 14px Lucida Grande; margin:0 0 18px;}
.titleH2 {font: normal 25px/27px Lucida Grande; color:#006496; padding:0 0 3px;}
.second-menu { font-size: 14px; border-top:2px solid #323232; margin:12px 0 0; line-height:41px;}
.mod-links {font-size:9px; padding: 0px 0 0; /*max-width:310px;*/}
.titleH1 {font:normal 26px Lucida Grande;}
.titleH3 {font:normal 15px Lucida Grande; color:#00aa32; text-transform:uppercase; padding:0 0 7px;}
.contentLayout {font-size:11px;}
TABLE, TD { font-size: 11px;}
.blog-tribuna .item {line-height: 14px; padding:8px 10px; font-size: 12px;}
}
