css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dobrochan.ru"),domain("dobrochan.org"),domain("dobrochan.com") {
.postbody {word-wrap: break-word !important;}
.replypost,.thread>form,.thread+span {display: block !important; max-width: 100% !important; width: auto !important; float: left !important;}
.replypost,.abbrev,.thread+span {clear: both !important;}
.replypost:first-of-type,.thread>form,.oppost .abbrev {clear: none !important;}
.replypost td {margin: 2px 0 !important;}
.replypost tbody,.replypost tr,.replypost td,.abbrev {display: block !important;}
td.doubledash {display: none !important;}
.replypost:first-of-type {max-width: -moz-calc(100% - 250px) !important;}
pre {white-space: pre-wrap !important;} code:after {white-space: pre-wrap !important; content: "\A";}
}

@-moz-document domain("iichan.hk"),domain("iichan.ru") {
blockquote {word-wrap: break-word !important;}
body>form>div:first-child table,#delform>div:first-child form,body>form>div+span {display: block !important; max-width: 100% !important; width: auto !important; float: left !important;}
body>form>div:first-child table,body>form>div+span {clear: both !important;}
body>form>div:first-child table:first-of-type,#delform>div:first-child form  {clear: none !important;}
body>form>div:first-child table td {margin: 2px 0 !important;}
body>form>div:first-child table tbody,body>form>div:first-child table tr,body>form>div:first-child table td,.omittedposts {display: block !important;}
body>form>div:first-child table td.doubledash {display: none !important;}
body>form>div:first-child table:first-of-type {max-width: -moz-calc(100% - 250px) !important;}
body>form>div:first-child table .reply::after {content:""; clear: both !important; display: block;}
}
