css:
/** read email colors  **/

.yO:not(.x7):not(.yQ):not(.aps) { background-color: rgba(170, 200, 140, 0.95) !important; color: rgb(0,20,0) !important;}


.xY:not(.apD):not(.apy):not(.apt) { border-bottom: 1px solid rgb(145, 181, 117) !important; }


.yO, .yO .y2 { color: rgb(77,101,60) !important; }


/** unread email colors  **/

.zA { background-color: rgba(222, 249, 204, 0.9) !important; color: rgb(0,0,0) !important;}


.zA .y2 { color: rgb(77,101,60) !important; }



/** alternating colors in messasges **/


div.h7:nth-of-type(odd) div.adn.ads, div.kv:nth-of-type(odd){ background-color: rgba(255,255,255,0.9); }

div.h7:nth-of-type(even) div.adn.ads, div.kv:nth-of-type(even){ background-color: rgba(230,250,230,0.9); }

div.kv div.G2 { background-color: transparent !important; }
