css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hordes.fr") {



/* Couleur écriture balise RP gras */
    .tid_editorContent strong {
        color : #B2E6EC !important;
        
/*Couleur titre sujet non lu*/        
        }
    #tid_forum .tid_threads .tid_unread .tid_title .tid_subject{
        color : white !important;
        }
/*Couleur background sujet non lu */        
    #tid_forum .tid_threads .tid_unread {
        background : #B5442D !important; 
    }
    
/* Supression flêche verte sujet sélectionné */        
    .tid_selectedIcon {
        display: none !important;
}
 /* Couleur background sujet sélectionné */
    .tid_threads .tid_selected .tid_title {
       background : #573C30 !important ;
      }

/* Couleur bouton "Envoyer */
    #tid_forum button {
    background : rgb(50, 25, 20) !important ;
    border-color : rgb(50, 25, 20) !important ;
   } 
   

}
