@-moz-document domain("www.dinorpg.com") {
    /* DinoRPG */
    
   .tid_buttonBar a
   {
        color: #E4AA69 !important;
        background-color: #B96426 !important;
        border-top: none !important;
        outline: 1px solid #E4AA69 !important;

   }
    
    .tid_tools .tid_wrapper .tid_buttonBar a
    {
