    #tid_forum button
    {
        color: #E4AA69 !important;
        background-color: #B96426 !important;
        border: none !important;
    }

  
    
    .tid_user.tid_userBg.tid_userUnknown
    {
        background-color: rgb(149, 80, 31) !important;
        border-top : 1px solid rgb(149, 80, 31) !important;
    }
    
    .tid_user.tid_userBg.tid_userFriend
    {
        background-color: #B96426 !important;
