css:
#toolbar-menubar,#PersonalToolbar{
background:rgb(240,240,240) !important;
}
#nav-bar{
background:-moz-linear-gradient(top,rgb(248,248,248),rgb(240,240,240)) !important;
}
.tabbrowser-tab[selected="true"]{
background-color:transparent !important;
background-image:-moz-linear-gradient(top,rgb(248,248,248),rgb(248,248,248)) !important;
}
.tabbrowser-tab:not([selected]),
.tabs-newtab-button{
background-image:-moz-linear-gradient(top,rgb(238,242,238),rgb(226,228,226) 6px,rgb(214,214,214) 93%,gray 100%) !important;
}
#addonbar-closebutton{
display:none;
}
#browser-bottombox{
background:rgb(240,240,240) !important;
}
#addon-bar{
max-height:25px !important;
}
#appmenuSecondaryPane,#appmenuPrimaryPane
{
background:RGB(240,240,240) !important;
}
#addons-page{
background:#EEE !important;
}
.toolbarbutton-1,.toolbarbutton-1 > toolbarbutton{
border:none !Important;
background:none !important;
box-shadow:none !Important;
margin-left:0 !important;
margin-right:0 !important;
padding-left:2px !important;
padding-right:2px !important;
}
