@-moz-document domain("www.google.com") {

/* General search page */
    /* hide center ad box */
    #taw {
        display: none !important;
    }

    /* hide right ad box */
    #vdls-adv {
        display: none !important;
    }

    /* hide bottom ad box */
    #bottomads {
        display: none !important;
    }

    /* hide right ad box (rhs_block) */
    #rhs_block table {
        display: none !important;
    }

/* Calendar */
    /* hide tasks toggler
     * note: there are no CSS parent selectors, so while this hides the tasks toggler,
     * it doesn't note remove the block or the dropdown image from DOM */
    div[title="Tasks"] {
        display: none !important;
    }
}

@-moz-document domain("www.youtube.com") {

    /* hide right ad box (homepage) */
    #homepage-sidebar-ads {
        display: none !important;
    }

    /* hide right ad box (video page) */
    .watch-pyv-vid {
        display: none !important;
    }
    #google_companion_ad_div {
        display: none !important;
    }
    #google_ads_frame1 {
        display: none !important;
    }
    #ad300x250 {
        display: none !important;
    }

    /* hide right ad box (search page) */
    .search-pva-content {
        display: none !important;
    }
    .sidebar-ads {
        display: none !important;
    }

    /* hide main ad box (search page) */
    .pyv-promoted-videos {
        display: none !important;
    }

    /* hide ad (float) */
    .ads-promoted {
        display: none !important;
    }
    .ad-div {
        display: none !important;
    }
    #search-pva-content {
        display: none !important;
    }
    #search-pva {
        display: none !important;
    }
    #pyv-ads {
        display: none !important;
    }

    /* hide top right banner ad */
    #watch-longform-ad {
        display: none !important;
    }

}
