Version: V2
Web: www.russh.com.ar
*/


.profile_picture_wrapper {
border: 3px solid gray;
-webkit-transform: rotate(-6deg);
-moz-transform: rotate(-3deg);
-moz-box-shadow: 0 3px 6px rgba(0,0,0,.10);
margin-left: -10px;
}
.profile-picture:hover {
-webkit-transform: scale(1.1);
-moz-transform: scale(1.1);
position: relative;
z-index: 5;
}
.profile-friends li:hover {
-webkit-transform: scale(1.1);
-moz-transform: scale(1.1);
position: relative;
z-index: 5;
}
#blueBar {
    background: -moz-linear-gradient(center top , #2E2E2E, #FFFFFF) repeat scroll 0 0 transparent !important;
}
}
