css:
@-moz-document domain("v2ex.com") {
a.top:link, a.top:visited, a.top:active {
color: 
#333;
text-decoration: none;
text-shadow: none;
font-size: 14px;
}
#Top{
background:#f4f4f4;
border-bottom: 1px solid 
#f4f4f4;
}
.item{
background:none;
}
a.balance_area:link, a.balance_area:visited, .balance_area
{
font-size: 12px;
line-height: 20px;
padding: 2px 12px;
-moz-border-radius: 3px;
border-radius: 3px;
box-shadow: 0px 1px 1px 
rgba(0, 0, 0, 0.15);
border: 1px 
#CCC solid;
}
#Rightbar .box > .cell:first-child > table:first-child{
display:none;
}
.item_node{background:none;}
#q{border: 1px solid 
#CCC;
background: 
#F9F9F9;
height: 18px;
margin: 2px 0px 1px 30px;
padding:3px 2px}
#Search  div{background:none !important;}
#Wrapper{
background-color: 
#EDEDED;
background-image: url("http://v2ex.com/static/img/shadow.png");
}
}
