css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://webdiplomacy.net/"), url-prefix("http://webdiplomacy.net/index.php") {

table.homeTable td.homeMessages { display: none !important;}
table.homeTable td.homeMessages + td.homeSplit { display: none !important; }

table.homeTable div.homeForumGroup { margin-bottom: 6px !important; }
table.homeTable div.homeForumGroup + div { display: none !important; }

table.homeTable td.homeGamesStats { width: 67% !important; }
table.homeTable td.homeGamesStats div.gamePanelHome { width: 49% !important; }
table.homeTable td.homeGamesStats > div:nth-child(4n+3) { border-right: 1px solid rgb(206, 206, 206); float: left !important; }
table.homeTable td.homeGamesStats > div:nth-child(4n+5) { border-left: 1px solid rgb(206, 206, 206); float: right !important; }
table.homeTable td.homeGamesStats > div:nth-child(4n+8) { display: none !important; }
table.homeTable td.homeGamesStats > div:nth-child(4n+2) { float: left !important; visibility: hidden !important; clear: both !important; }

}
