css:
/*code by lluvia #3245*/

@-moz-document 
url-prefix("http://www.mysticalequine.com/messages.php?"),
url-prefix("http://mysticalequine.com/messages.php?")
{
.table{height: 500px !important;overflow: auto !important; max-height:500px !important;
scrollbar-base-color:#0C194B;
scrollbar-track-color:#0C194B;
    scrollbar-highlight-color:#0C194B; 
scrollbar-3dlight-color:#474A8C; 
scrollbar-shadow-color:#0C194B; 
scrollbar-darkshadow-color:#474A8C; 
scrollbar-arrow-color:#474A8C;
}

::-webkit-scrollbar-thumb:horizontal {background-color:#474A8C; height:12px !important;}
::-webkit-scrollbar {height:7px; width:15px; background-color:#b2b2c8;}
::-webkit-scrollbar-thumb:vertical {background-color:#474A8C; height:auto;}

::-webkit-scrollbar-track-piece {
  background-color: #0C194B; }
::-webkit-scrollbar-thumb:vertical {
  height: 100px;
  background: -webkit-gradient(linear, left top, right top, color-stop(0%, #474A8C), color-stop(100%, #0C194B));
  border: 1px solid #474A8C;
  border-top: 1px solid #474A8C;
  border-left: 1px solid #474A8C; }

::-webkit-scrollbar-thumb:horizontal {
  width: 100px;
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #474A8C), color-stop(100%, #0C194B));
  border: 1px solid #474A8C;
  border-top: 1px solid #474A8C;
  border-left: 1px solid #474A8C; }
}
