css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url(chrome://messenger/content/msgAccountCentral.xul) {

  #acctCentralGrid {
    background-color: -moz-Field !important;
    color: -moz-FieldText !important;
  }

  #acctCentralHeaderRow {
    color: inherit !important;
  }

  .acctCentralRowTitleBox {
    background-color: -moz-CellHighlight !important;
    color: -moz-CellHighlightText !important;
  }

  .acctCentralLinkText {
    color: -moz-FieldText !important;
  }

  .acctCentralLinkText:hover,
  .acctCentralLinkText:focus {
    color: -moz-HyperlinkText !important;
  }

  .acctCentralLinkText:active {
    color: -moz-ActiveHyperlinkText !important;
  }

}
