@-moz-document url-prefix("http://www.nicovideo.jp/"), url-prefix("https://www.nicovideo.jp/") {
/* （く）追加分 */
/* 総合トップ */
/* 広告要素 */
.TopPage-headerAd,
div[class*="billboardAdContainer"], /* ビルボード */
.PcUniTop970x250BillboardAdContainer,
.PcUniTop300x250EastAdContainer,
.PcUniTop300x60TextMiddleEastAdContainer,
.PcUniTop300x60TextMiddleEastAdContainer,
.WebPcPrtextAdContainer,
.PcUniTop350x240MiddleEastAdContainer,
.PcUniTop728x90SouthAdContainer,
.SpecialEventLink,
/* 関連サービスへの誘導バナー */
.EnjoyLargeContainer,
.EnjoySmallContainer,
.FeatureContainer /* 今日のおすすめ特集コンテナ */
{display:none!important}

/* 総合トップ 検索バー 修正 */
/* .TopPage-header .TopPage-headerMain {height:32px!important} */
.TopPage-header .LogoContainer {margin-bottom:0!important}
.TopPage-header .LogoContainer, .TopPage-header .SearchContainer {display:inline-block!important;vertical-align:middle!important}

/* 総合トップ ニコニコからのお知らせトップマージン修正 */
.NiconicoNoticeContainer {margin-top:0!important}

/* 総合トップ 動画と生 2列2行処理 */
.TodayRecommendContainer-block .ContentItem--large, .NicovideoContainer .ContentItem--large, .PastNicoliveContainer .ContentItem--large {display:flex}
.TodayRecommendContainer-block .ContentItem-meta, .NicovideoContainer .ContentItem-meta, .PastNicoliveContainer .ContentItem-meta {margin:8px 0 0}
.TodayRecommendContainer-block .TodayRecommendContainer-contents, .TodayRecommendContainer-block .TodayRecommendContainer-lives, .NicovideoContainer .NicovideoContainer-content, .PastNicoliveContainer .BaseMainContentsContainer-contents {display: -webkit-box;display: -ms-flexbox;display: flex;-webkit-box-pack: justify;-ms-flex-pack: justify;justify-content: space-between;-ms-flex-wrap: wrap;flex-wrap: wrap;}
.TodayRecommendContainer-block .ContentItem-thumbnail, .NicovideoContainer .ContentItem-thumbnail, .PastNicoliveContainer .ContentItem-thumbnail, .TodayRecommendContainer-block .ContentItemSkeleton-thumbnail, .NicovideoContainer .ContentItemSkeleton-thumbnail, .PastNicoliveContainer .ContentItemSkeleton-thumbnail {width:151px!important;height:85.5px!important}
.TodayRecommendContainer-block .ContentItem--large, .TodayRecommendContainer-block .ContentItem, .NicovideoContainer .ContentItem--large, .NicovideoContainer .ContentItem, .PastNicoliveContainer .ContentItem--large, .PastNicoliveContainer .ContentItem {display:block!important;width:151px!important}
.TodayRecommendContainer-block .ContentItem+.ContentItem, .NicovideoContainer .ContentItem+.ContentItem, .PastNicoliveContainer .ContentItem+.ContentItem {margin-top:0}
.TodayRecommendContainer-block .ContentItem:nth-child(n+3), .NicovideoContainer .ContentItem:nth-child(n+3), .PastNicoliveContainer .ContentItem:nth-child(n+3) {margin-top:16px}
/* 総合トップ 静画の3列2行処理 */
.NicoseigaContainer .ContentItem, .NicoseigaContainer .ContentItemSkeleton {width:96px}
.NicoseigaContainer .ContentItem-thumbnail, .NicoseigaContainer .ContentItemSkeleton-thumbnail {width:96px;height:96px}
.NicoseigaContainer .ContentItem:nth-of-type(3), .NicoseigaContainer .ContentItemSkeleton:nth-of-type(3)
{margin-top:0}
/* 総合トップ 共通 タイトルの2行クランプ処理 */
.TodayRecommendContainer-block .ContentItem-title, .NicovideoContainer .ContentItem-title, .PastNicoliveContainer .ContentItem-title, .NicoseigaContainer .ContentItem-title {-webkit-line-clamp:2!important}
/* 総合トップ 今日のオススメ5個目消去 (アイテム表示順はランダムで優先順位が無いので1つ削っても問題ないと判断)) */
.TodayRecommendContainer-contents .ContentItem:nth-child(n+5) {display:none!important}

/* 動画トップ */
/* 広告要素 */
.HeaderContainer-ads,
.VideoTop-billboard,
.RecommendAreaContainer-banners,
.Ads300x60WebPc360,
.AdsPRText,
.NewArrivalVideosContainer-columnRight,
.Ads728x90WebPcFooter,
/* 動画各カテゴリ*/
/* 広告要素 */
.Ads300x250WebPcPrime
{display:none!important}

/* 動画トップ おすすめ動画 1行6列化 */
.RecommendAreaContainer-columnLeft {width:1024px!important}
.RecommendAreaContainer .RecommendVideoContainer .VideoCard {width:160px!important}
.RecommendVideoContainer .VideoCard:nth-child(n+4) {margin-top:0!important}
.RecommendAreaContainer .RecommendVideoContainer .VideoCard:nth-child(n+6) {margin-top:0!important}
.RecommendAreaContainer .RecommendVideoContainer .VideoItem:nth-child(n+7) {margin-top:24px!important} /* 公式オススメ増量時の保険 */
/* 外部サービス(flog)によるオススメと明示されていないので削除 */
.RecommendVideoContainer-flog {display:none!important}
/* .RecommendVideoContainer .RecommendVideoContainer-flog:last-child {margin-right:345.6px!important} */

/* 動画トップ ランキング 1行6列化 */
.RankingMatrixContainer-columnRight {display:none!important}
.RankingMatrixContainer-columnLeft {width:1024px!important}
.RankingVideosContainer .VideoCard {width:160px!important;min-height:137px!important}
.RankingVideosContainer .VideoCard:nth-child(n+4) {margin-top:0!important}

/* 動画トップ 新着動画 2行6列化 */
.NewArrivalVideosContainer-columnLeft {width:1024px!important}
.NewArrivalVideosContainer .VideoCard:nth-child(n+5) {margin-top:0!important}
.NewArrivalVideosContainer .VideoCard:nth-child(n+7) {margin-top:24px!important}

/* 動画カテゴリ別 人気のタグ */
.TrendyTagsContainer-list {height:138px} /* 三行までに縮小 */
.TrendyTagAreaContainer-columnRight {display:none!important}
.TrendyTagAreaContainer-columnLeft {width:1024px!important}

/* 動画カテゴリ別 新着コメント動画 2行6列化 */
.NewCommentVideosContainer-columnRight {display:none!important}
.NewCommentVideosContainer-columnLeft {width:1024px!important}
.NewCommentVideosContainer[data-has-billboard=false] .VideoCard:nth-child(n+9) {display:block!important}
.NewCommentVideosContainer[data-has-billboard=false] .VideoCard:nth-child(n+5) {margin-top:0!important}
.NewCommentVideosContainer[data-has-billboard=false] .VideoCard:nth-child(n+7) {margin-top:24px!important}
/* 動画カテゴリ別 動画ランキング */
.RankingVideosContainer-columnRight {display:none!important}
.RankingVideosContainer-columnLeft {width:1024px!important}
.RankingVideosContainer .VideoItem {width:160px!important;min-height:0!important}
.RankingVideosContainer .VideoItem:nth-child(n+4) {margin-top:0!important}

/* 動画トップ&カテゴリ ヘッダ検索バー */
.HeaderContainer-searchBox {justify-content:left!important}
.SearchBoxContainer .SearchBox {width:auto!important}
.SearchBoxContainer .SearchBox-optionTab {float:left!important;padding-top:5px!important}
.SearchBoxContainer .SearchBox-form {margin-left:200px!important}
/* （く）追加分ここまで */
#hidariue, .hidariue, #header /* 左上 */
#web_pc_top, #head_ads, #PAGEHEADER .noticeBlock, .notice.banner, /* ヘッダ 右 広告 */
#enjoy, /* 左カラム 告知 */
#tenku, /* 中カラム 最上段 */
#mainContents > div:nth-child(1):not(#today), /* 中カラム 今日のオススメ情報上部の不定期告知 */
