css:
@-moz-document domain("osu.ppy.sh") {

#tablist + div {height:0px !important;overflow:hidden !important;}

#tablist:hover + div{border-radius:5px;  height:223px !important;/* height:auto !important;*/
  overflow:hidden !important;-webkit-transition: .5s;-moz-transition: .5s;-o-transition: .5s ;}

h1       {background:#eeb !important;box-shadow:2px 7px 7px #aaa;margin:0px 0px 10px 0px;border-radius:13px;padding-left:30px }
h1:hover {background:#ebb !important;box-shadow:3px 8px 10px #aaa; 
-webkit-transition: .3s ease-out;
-moz-transition: .3s ease-out;
-o-transition: .3s ease-out;
}

.beatmapDownloadButton{border:0px solid #000;padding:4px}
.beatmapDownloadButton:hover{background: #fbb;
-webkit-transition: .5s;
-moz-transition: .5s;
-o-transition: .5s ;}





img#profimage
{ position:fixed !important;top:-45px;align:auto !important;}

img#profimage:hover
{ position:fixed !important;top:0px;
-webkit-transition: .2s ease-out;
-o-transition: .2s ease-out;
-moz-transition: .2s ease-out;
;}

}

@-moz-document url-prefix("http://osu.ppy.sh/p/beatmaplist") {
.bmlist-options  {display:block !important}
.left-aligned{display:none !important}
}
