css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("friendfeed.com") {

/*Links Colors*/

a {text-decoration: none !important; color: #2A7FFF!important; } 
a:link { text-decoration: none !important; color: #2A7FFF !important;  }
a:visited { text-decoration: none; color: #103878 !important; }
a:hover { text-decoration: none !important;color: #24A30B !important;  }
a:active { text-decoration: none !important;color: #EF9B08 !important;  }

/*Feed*/

.feed {
font-size : 9.5pt;
font-family : Tahoma;
direction : rtl;
}
.summary {
text-align: left;
font-family : Tahoma;
}
.info {
direction : ltr;
text-align: right;
font-family : Tahoma;
}

/*realtime*/

.updates .entry{
direction: rtl;
font-family: Tahoma;
}
.updates .entry .commentform textarea{
font-family: Tahoma;
direction:rtl;
}

/*Separates each posts*/

.cluster{
border-bottom:1px dotted gray !important; 
padding-bottom:1em !important;
}

/*Comments*/

.feed .entry .comment .content{
margin-right:31px!important
}

.feed .entry .comments .comment .quote {
float:right!important;
}

.feed .entry .commentform textarea{
font-family : Tahoma !important;
}

/*---Likes---*/

.feed .entry .info,.feed .entry .likes{
direction:ltr!important;
text-align:left!important;
}

/*Comment Highlighting*/

.body .entry .comment.friend {
background-color: #ffffe0 !important;
}
.body .entry .comment.owner {
background-color: #ecf2fa !important;
}

/*Serach and sharebox*/

.query{
font-family : Tahoma!important;
direction : rtl !important;
}

.sharebox textarea.title{
direction : rtl !important;
font-family : Tahoma !important;
font-size : 8pt !important;
}

/*User graybox*/
td{
font-family:Tahoma !important;
}
}
