css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ageofconan.com") {

#content{
background: none !important;
background-color: #FFFFFF !important;
}

#top{
background: none !important;
background-color: #FFFFFF !important;
}

body {
background: none !important;
background-color: #FFFFFF !important;
color: #000000 !important;
}

.menu, a:link, a:active, a:visited, a:hover {
color:#225533 !important;
}

.page, #posts, tbody, td, tr, div, table, .alt1, .alt2, .thead, .tcat, .alt1Active, .tfoot, .smallfont {
background: none !important;
background-color: #FFFFFF !important; 
color: #000000 !important;

}

.bigusername {
background-color: #bbCCbb !important; 
border-width: thin !important;

}

tbody tr td a img {
display:none;
}

.smallfont a img{
display: inline;
}


img {
display: none !important;
}


}
