css:
/*
 * style for location bar autocomplete menu
 * you can also modify "browser.urlbar.maxRichResults" value to config drop-down menu length or item amount
 */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
.ac-comment {font-size:100% !important;color: #000077 !important;padding-left:10px !important}
.ac-url-text {font-size:100% !important;color: #444444 !important;padding-left:10px !important}
.ac-comment[selected=true],.ac-url-text[selected=true] { color: #FFFFFF !important}
