@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("intuit.ru") {

#lecture_annotation, body, p, td {
font: 12px Verdana,Arial,Tahoma,"Trebuchet MS",sans-serif !important;
color: #454545 !important;
}
.rtitle {
font-weight: normal !important;
}
.objectName {
font: 12px Verdana,Arial,Tahoma,"Trebuchet MS",sans-serif !important;
font-weight: normal !important;
color: #454545 !important;
}
b { 
font-size: 12px;
font-family: Verdana,Arial,Tahoma,"Trebuchet MS",sans-serif !important;
color: #454545 !important;
}
h3 {
font: 16px Verdana,Arial,Tahoma,"Trebuchet MS",sans-serif !important;
font-weight: normal !important;
}
.attention {
font-weight: normal !important;
color: #4545A5 !important;
}
.lecture_title {
font: 14px Verdana,Arial,Tahoma,"Trebuchet MS",sans-serif !important;
color: #454585 !important;
}
.example, .texample {
font: 12px Monospace !important;
color: #4545A5 !important;
}
.example {
font-size: 11px !important;
background-color: #fff;
border-left: 3px solid #ddd;
padding: 0px 0 0px 5px;
}

#y5_direct1 {
display: none;
}

.topbanner, .rtxt, td[width="160"], .lecture_write_to_us, #lecture_banner, .adv_top, .copy, .border {display:none}
}
