css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://live.nicovideo.jp/watch/nsen/") {

#wrapSlide div#cntBox div#playlist.leftCnt,
#wrapSlide div#cntBox div.rightCnt h3#h3Playlist,
#wrapSlide div#cntBox div.rightCnt div#historyReloadLock,
#wrapSlide div#cntBox div.rightCnt ol#history,
#moreButton, #zapping_area, .headerInner > p
    { display:none !important; }

#wrapSlide {
	border-top: none !important;
	background: transparent !important;
}

#requestContainer {
	position: relative !important;
	top: -110px !important;
}

#wrapSlide > #cntBox > .rightCnt {
	position: absolute !important;
	top: -127px !important;
	right: 20px !important;
}
#playingBox {
	width: 600px !important;
}
#h3Playing {
	position: absolute !important;
	top: -10px;
	right: 10px;
	z-index: 10;
}


#player_btm {
	position: relative !important;
	top: -108px;
}
#header {
	margin-top: 10px !important;
}

#all_cover {
	padding-top: 30px !important;
}

}

/*flvplayer_container のボーダーラインの色合わせ　*/

@-moz-document url-prefix("http://live.nicovideo.jp/watch/nsen/vocaloid") {
    #flvplayer_container {
    	border-bottom: 4px solid #027873 !important;
    }
}
@-moz-document url-prefix("http://live.nicovideo.jp/watch/nsen/sing") {
    #flvplayer_container {
    	border-bottom: 4px solid #CF6300 !important;
    }
}
