css:
@-moz-document domain("www.reddit.com") {
* {
font-family: helvetica !important;
}

#sr-header-area {
padding-top: 2px !important;
}

#header {
border-bottom: 1px solid #aaa !important;
}

#header-img {
background: url('http://i.imgur.com/TuJj9.png') 70% 50% no-repeat !important;
max-height: 50px !important;
max-width: 200px !important;
}

#header-bottom-left {
background: #fff !important;
}
#header-bottom-right {
background: #fff !important;
margin-top: 5px !important;
}

a, li.first a.comments {
color: #078 !important;
}
a:hover, span.hover.pagename.redditname a {
color: #d70 !important;
}

div.thing.link {
margin: 20px 20px 0 20px !important;
padding-bottom: 20px !important;
border-bottom: 1px solid #ddd !important;
}

div.thing.link .entry {
padding-left: 20px !important;
}

div.last-clicked {
border: none !important;
}

div.side {
display: none !important;
}

ul.tabmenu li a {
background: none !important;
}ul.tabmenu li {
padding: 5px !important;
}

#userbarToggle {
display: none !important;
}

div.score {
color: #d70 !important;
}

div.entry > .tagline {
color: #fff !important;
}
div.entry > .tagline > span {
display: none !important;
}
div.entry > .tagline a:nth-of-type(1) {
display: none !important;
}
div.entry > .tagline a:nth-of-type(3) {
position: absolute;
right: 50px;
}

.flat-list.buttons .share {
display: none !important;
}
.flat-list.buttons a {
color: #ccc !important;
}

div.usertext-body div.md {
margin: 12px 0 12px 10px !important;
}

.score.unvoted, .score.likes {
color: #d70 !important;
font-weight: bold;
}

.res_comment_ups {
opacity: .5;
}
.res_comment_downs {
opacity: .5;
}

.single-page div.content:nth-of-type(3) {
margin-top: 0 !important;
}

.comments-page .content > #siteTable.sitetable.linklisting > div {
background: #F8F8F8 !important;
padding-top: 20px !important;
margin: 0 -5px 20px -5px !important;
}

.content div.commentarea {
margin-top: -40px !important;
background: #fff !important;
}

div.clearLeft {
display: none !important;
}

#siteTable.sitetable.linklisting > div > div:nth-of-type(2) {
border: none !important;
}

.comments-page #header-bottom-left .tabmenu .selected a {
background: #F8F8F8 !important;
border-top: 1px solid #aaa !important;
border-left: 1px solid #aaa !important;
border-right: 1px solid #aaa !important;
border-bottom: 1px solid #F8F8F8 !important;
}
}
