css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("flickr.com") {
#head #head-top #head-status, .FlickrLogo {padding-top: 0 !important;}
#head #head-nav-bar { margin-top: 0 !important;}
#head #head-top #head-logo img, #main {margin-top: 0 !important;}
#head {min-height: 30px !important;}
#head-min-right {margin-top:5px !important;}
#upload-me-nav {margin-top:14px !important;}
#nav {margin: 3px 0 !important;}
h1.photo-title {margin: 1px 0 !important;}
#sidebar {margin-top:3px !important}
#photo-story {margin-bottom:5px !important;}
#sidebar-contexts h4, #sidecar .photos-people h4, #sidecar h4, #sidebar-contexts h4  {margin-top:10px !important;}
#primary-context { margin-bottom: 10px !important;}
#options-menu ul li a {padding: 0 7px 0 32px !important;}
#options-menu ul li a span {top: 0 !important;}
.yui3-popover { margin-top: -1px; }
#photo-story-attribution {position:relative; top:4px;}
table.launchHead {margin:0 !important;}
ul.tt-activity li.act-item { padding-top: 0 !important; padding-bottom:8px !important; }
.tt-block {    margin-bottom: 0 !important;}
#main > h1 {padding-top:5px !important;}
#SubNav { margin-top: 3px !important; margin-bottom:3px !important; }
div.share-this-wrapper { position: relative; top: 18px;}
#SubNav .Section h1 {margin-bottom:5px !important;}
.PhotoStream {    margin-top: 4px !important;}
.searchified-options {margin-bottom:5px !important;}
body.breakout .photo-display-container { padding-top: 0 !important; }
#working-area-controls {padding: 4px 16px 0 !important;}
#editr-panel {margin-top:56px !important;}
#editr-panel-scroll-wrapper h3 { padding: 3px 0 4px 16px !important; font-size: 14px !important;}
#editr-panel #editr-panel-title-description {  padding-top: 3px !important;}
#editr-panel #editr-panel-title-description .display, #editr-panel #editr-panel-title-description .widget {  padding-bottom: 3px !important;}
#editr-panel-form ul li h4 {padding-top:8px !important; padding-bottom:8px !important; font-size:13px !important;}
.editr-option .widget {padding-bottom: 8px !important;}
.editr-options .icons { margin-bottom:-20px !important;}
#editr-panel-owner-settings .owner-settings-options { padding: 0 !important;}
#editr-panel-owner-settings.editing .display {  padding-bottom: 2px !important;}
.photo-display-container.ju .photo-display-item .meta, .zoomer .meta {text-shadow: 1px 1px 1px black !important;} /* blurred shadow in justified view */
}
