css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("workflowy.com") {



/* Hidden */
#share_buttons, .corner, .top, .bottom, .left, .right, #bottomLinks {display:none;}


/* Logo
#logo {background:url(http://i.imgur.com/sJ7Wp.png) no-repeat!important;}
*/


/* Global */
body {
font-size:15px;
padding-top:0;
}


/* Dickbar */
#header {
box-shadow: 0 4px 16px #333;
border-bottom: 1px solid #AAA!important;
background: #353535!important;
}


/* Page */
.page {
border: 1px solid #DFDFDF;
box-shadow: 0 0 15px #F3F3F3;
}


/* Work Surface */
.name {padding-top: 10px;}
.project {margin-bottom: 15px;}
.project .children .open {margin-top: 10px;}


/* Links */
.mainTreeRoot a[href^="http://"], 
.mainTreeRoot a[href^="https://"], 
.mainTreeRoot a[href^="ftp://"] {color: steelBlue;}
 
}
