css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kirik.tea-nifty.com") {

body {
  background-color: #333333 !important;
}

font[color="white"], .entry-content p, div.entry-body, .entry-footer-info, .entry-footer-info a, .content-nav {
  color: #333333 !important;
}

.entry-content p, .div.entry-body {
  line-height: 1.8em !important;
}

.entry-content {
  padding: 25px 0 22px 0 !important;
}

#pagebody, #alpha-inner, .layout-two-column-right #alpha-inner {
  background-color: #ffffff !important;
}

.entry-more a, .entry-body a, .entry-more-link a {
  color: #147316 !important;
}

.layout-two-column-right #beta {
  margin-right: 0 !important;
}

.content-nav a {
  color: #666666 !important;
}

}
