css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url(chrome://mozapps/content/extensions/extensions.xul), url(about:addons)
{
    /* perfect relnotes style */
    .relnotes-toggle {
        border: 1px solid rgba(60, 73, 97, 0.5) !important;
    }
    /* release notes drop down moved above release notes */
    .relnotes-container + hbox {
        -moz-box-ordinal-group: 2 !important;
    }
    .relnotes-container {
        -moz-box-ordinal-group: 3 !important;
    }
    .relnotes-header {
        display: none !important;
    }
    /* don't move controls on relnotes-toggle */
    #updates-list .advancedinfo-container {
        -moz-box-align: start !important;
    }
    #updates-list .update-available {
        margin-top: 2px !important;
    }
}
