css:
body {
    color: #999; background: #272727;
}
h1 {
    color: #444; text-shadow: 1px 1px 0 rgba(255, 255, 255, .3);
}
#topline {
    font-size: 90%;
}
#playlistsWrap {
    padding-top: 50px;
}
#list h2 .song {
    color: #EDEDED;
}
#list h2 .interpreters {
    color: #999;
}
#list .divider {
    opacity: .1;
}


/**
 * ACTIVE
 */
#list .clip.active {
    background-color: #1C1C1C;
    box-shadow: 0 2px 4px #000, inset 1px 1px 1px #444;
}
#list .active h2 .song {
    color: #EDEDED;
}
#list .active h2 .interpreters {
    color: #999; font-size: 85%; font-weight: normal;
}
#list .clip:hover {
    box-shadow: 0 0 1px 1px #C4302B;
    background: #1C1C1C;
}

#login a,
#login a:hover,
#seznam,
#more {
    border: 1px solid #333;
    color: #999;
    font-weight: bold;
    text-shadow: 1px 1px 1px #000;
    box-shadow: 0 1px 7px #000;
    background: #1C1C1C;
    background: -moz-linear-gradient(top, #272727 50%, #1C1C1C 100%);
    background: -webkit-linear-gradient(top, #272727 50%, #1C1C1C 100%);
    background: -o-linear-gradient(top, #272727 50%, #1C1C1C 100%);
    background: -ms-linear-gradient(top, #272727 50%, #1C1C1C 100%);
    background: linear-gradient(top, #272727 50%, #1C1C1C 100%);
}
#login a,
#login a:hover,
#seznam {
    box-shadow: 0 1px 3px #000;
}
#login a:hover,
#seznam:hover,
#more:hover {
    color: #EDEDED;
    border-color: #444;
}
